mapper.select:


SELECT member_name
FROM t_member
WHERE member_id = #{memberPw} AND member_pw = #{memberPw}



SELECT member_password
FROM t_member
WHERE member_id = #{memberPw}



SELECT member_id, member_name, member_pw
FROM t_member
WHERE member_id = #{memberId}


SELECT member_name, member_id
FROM t_member
WHERE member_id='${memberId}';
