import React from "react"
import axios from "axios"
import Event from "../components/Event"

class Main extends React.Component{
    state = {
        isLoggedIn: false,
        isEventsLoading: true,
        isGroupsLoading: true,
        events: [{
            id: 1,
            title: "Meet up for learning English",
            description: "This meeting is about...",
            date: "2021년 6월 15일 금요일 오전 11시부터 오후2시",
            location: "대구 혁신도시"
        }],
        groups: [{
            id: 999,
            name: "Studium",
            numOfMembers: 240,
            tags: "기술"
        }]
    };

    render() {
        const {
            isLoggedIn,
            isEventsLoading,
            isGroupsLoading,
            events, 
            groups
        } = this.state;

        return (
            <div className="container">
                <div className="main_image">
                    <h3>직접 만나 공부해볼까~!</h3>
                    <p>원하는 그룹을 찾아 함께 공부하는 경험을 즐겨보세요.</p>
                    <button>Studium 가입하기</button>
                </div>
                <div className="events-wrap">
                    <h4>내 주변 이벤트</h4>
                    <p>근처에서 곧 진행될 이벤트를 확인해보세요</p>
                    <div className="event">
                        {isEventsLoading ? "Loading.." : events.map(event => (
                            <Event
                                id = {Event.id}
                                title = {Event.title}
                                description = {Event.description}
                                date = {Event.date}
                                location = {Event.location}
                            />        
                        ))}
                    </div>
                </div>
            </div>
        )
    }

    //Methods below are not fully implemented yet

    //Get events from DB
    getEvents = async () => {
        /*
        const {
            data : {
                data : {events}
            }
        } = await axios.get("request url")
        this.setState( {events: events, isEventsLoading: false} )
        */
       this.setState( {isEventsLoading: false} )
    }

    //Get groups from DB
    getGroups = async () => {
        /*
        const {
            data : {
                data : {groups}
            }
        } = await axios.get("request url")
        this.setState( {groups: groups, isGroupsLoading: false} )
        */
        this.setState( {isGroupsLoading: false} )
    }

    componentDidMount() {
        this.getEvents();
        this.getGroups();
    }

}

export default Main;
