import React from "react"
import axios from "axios"
import { Link } from "react-router-dom"
import Event from "../../components/event/Event"
import Group from "../../components/group/Group"
import Category from "../../components/category/Category"
import './main.css'

class Main extends React.Component{
    state = {
        isLoggedIn: false,
        isEventsLoading: true,
        isGroupsLoading: true,
        isCategoriesLoading: true,
        events: [
            {
                id: 1,
                title: "Meet up for learning English",
                description: "This meeting is about...",
                date: "2021.06.21 FRI 10:00 - 12:00",
                location: "대구 혁신도시"
            },
            {
                id: 2,
                title: "1:1 Language Exchange",
                description: "This meeting is about...",
                date: "2021.06.23 SAT 11:00 - 13:00",
                location: "대구 율하역"
            },
            {
                id: 3,
                title: "Learning AWS",
                description: "This meeting is about...",
                date: "2021.06.30 MON 13:00 - 16:00",
                location: "대구 동성로"
                }
        ],
        groups: [
            {
                id: 999,
                image: "https://awssofia.com/wp-content/uploads/2020/01/cropped-AWS-Website-Banner.jpg",
                name: "AWS Korea Community",
                numOfMembers: 240,
                tags: "기술"
            },
            {
                id: 998,
                image: "https://res.klook.com/images/fl_lossy.progressive,q_65/c_fill,w_1200,h_630,f_auto/w_80,x_15,y_15,g_south_west,l_klook_water/activities/xfarkb2lvobut3c5jugs/%20Language%20Exchange%20Experience%20in%20Seoul.jpg",
                name: "영어회화 배우기",
                numOfMembers: 338,
                tags: "외국어"
            },
            {
                id: 997,
                image: "https://pacificenglishschool.com/wp-content/uploads/2020/06/banner_study-scaled.jpg",
                name: "정보처리기사 필기",
                numOfMembers: 102,
                tags: "자격증"
            }
        ],
        categories: [
            {
                image: "https://cdn.dribbble.com/users/879147/screenshots/7906715/media/1b1d2ef35dab525ed63b8b5816813132.jpg?compress=1&resize=400x300",
                name: "기술"
            },
            {
                image: "https://t1.daumcdn.net/liveboard/bookclub/93ff3a3b875349dda49bb3c9581a4532.jpeg",
                name: "외국어"
            },
            {
                image: "https://image.freepik.com/free-vector/online-certification-illustration_23-2148575512.jpg",
                name: "자격증"
            }
        ]
    };

    render() {
        const {
            isLoggedIn,
            isEventsLoading,
            isGroupsLoading,
            isCategoriesLoading,
            events, 
            groups,
            categories
        } = this.state;

        return (
            <div className="container">
                <div className="main-image">
                    <div className="main-letter">
                        <h3>직접 만나 공부해볼까~!</h3>
                        <p>원하는 그룹을 찾아 함께 공부하는 경험을 즐겨보세요.</p>
                        <Link to="/register">
                            <button>STUDIUM 가입하기</button>
                        </Link>
                    </div>
                </div>
                <div className="content-area">
                    <div className="events-wrap">
                        <p className="section-title">내 주변 이벤트</p>
                        <p className="section-detail">근처에서 곧 진행될 이벤트를 확인해보세요</p>
                        <div className="event">
                            {isEventsLoading ? this.displayLoading() : events.map(event => (
                                <Event
                                    id = {event.id}
                                    title = {event.title}
                                    description = {event.description}
                                    date = {event.date}
                                    location = {event.location}
                                />        
                            ))}
                        </div>
                    </div>
                    <div className="groups-wrap">
                        <p className="section-title">내 주변 그룹</p>
                        <p className="section-detail">나랑 같이 공부할 그룹 찾기</p>
                        <div className="group">
                            {isGroupsLoading ? "Loading.." : groups.map(group => (
                                <Group
                                    id = {group.id}
                                    image = {group.image}
                                    name = {group.name}
                                    numOfMembers = {group.numOfMembers}
                                    tags = {group.tags}
                                />        
                            ))}
                        </div>
                    </div>
                    <div className="category-wrap">
                        <p className="section-title">카테고리</p>
                        <p className="section-detail">관심 있는 주제로 그룹을 찾아보세요.</p>
                        <div className="category">
                            {isCategoriesLoading ? "Loading.." : categories.map(category => (
                                <Category
                                    image = {category.image}
                                    name = {category.name}
                                />
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        )
    }

    //Methods below are not fully implemented yet

    //Display loading..
    displayLoading(){
        document.write("로딩중입니다...")
    }
    //Get events from DB
    getEvents = async () => {
        /*
        const {
            data : {
                data : {events}
            }
        } = await axios.get("request url")
        this.setState( {events: events, isEventsLoading: false} )
        */
        setTimeout(() => this.setState( {isEventsLoading: false} ), 3000);
        //this.setState( {isEventsLoading: false} )
    }

    //Get groups from DB
    getGroups = async () => {
        /*
        const {
            data : {
                data : {groups}
            }
        } = await axios.get("request url")
        this.setState( {groups: groups, isGroupsLoading: false} )
        */
        this.setState( {isGroupsLoading: false} )
    }

    //Get categories from server
    getCategories = async () => {
        this.setState( {isCategoriesLoading: false} )
    }

    componentDidMount() {
        this.getEvents();
        this.getGroups();
        this.getCategories();
    }

}

export default Main;
